import React, { useEffect, useState } from "react";

function Chat({socket,username,room}) {

    const [currMessage, setCurrentMessage] = useState("");
    const [messageList, setMessageList] = useState([]);

    const sendMessage = async () => {
        if(currMessage!== ""){
            const messageData = {
                room: room,
                author: username,
                message: currMessage,
                time: new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
            }

            await socket.emit("send_message", messageData);
            setMessageList( (list) => [...list, messageData]);
        }
    }

    useEffect( () => {
        socket.on("rec_msg", (data) => {
            setMessageList( (list) => [...list, data]);
        })
    }, [socket])

    return(
        <div className="chat-window">
            <div className="chat-header">
                <p>Live Chat</p>
            </div>
            <div className="chat-body">
                {messageList.map( (messageContent) => {
                    return (
                    <div className="message" id={username === messageContent.author ? "you" : "other"}>
                        <div>
                            <div className="message-content"><p>{messageContent.message}</p></div>
                            <div className="message-meta">
                                <p>{messageContent.time}</p>
                                <p>{messageContent.author}</p>
                            </div>
                        </div>
                    </div>);
                })}
            </div>
            <div className="chat-footer">
                <input type="text" placeholder="Message.." onChange={(event) => setCurrentMessage(event.target.value)}/>
                <button onClick={sendMessage}>&#9658;</button>
            </div>
        </div>
    );
}

export default Chat;
